(a_hr_talent, 'What is an ATS?', 'multiple_choice', '["Applicant Tracking System for managing recruitment","Automated Testing System","Annual Training Schedule","Advanced Technology Service"]', 'Applicant Tracking System for managing recruitment', 5, 9),
(a_hr_talent, 'Candidate experience affects:', 'multiple_choice', '["Employer brand, offer acceptance rate, and referrals","Only the candidate","Only the recruiter","Only salary"]', 'Employer brand, offer acceptance rate, and referrals', 5, 10),
(a_hr_talent, 'What is a job competency model?', 'multiple_choice', '["A framework defining skills and behaviors needed for a role","A salary structure","An org chart","A training manual"]', 'A framework defining skills and behaviors needed for a role', 5, 11),
